// export default TypingEffect;


import { motion } from "framer-motion";

const text1 = "Are you ready to change the landscape of education with LearnHyve? We bring advanced tools, a vibrant community, and hands-on experiences.";
const text2 = "Discover a dynamic gamified learning platform that equips students with essential life and soft skills. Unlock a new way of learning with LearnHyve!";

// Function to animate text letter by letter
const generateLetterVariants = {
  hidden: { opacity: 0 },
  visible: (index) => ({
    opacity: 1,
    transition: { delay: index * 0.02 }, // 20ms delay between each letter
  }),
};

const AnimatedText = ({ text }) => {
  return (
    <motion.p 
      className="text-lg text-center text-black max-w-2xl mx-auto leading-relaxed"
      initial="hidden"
      animate="visible"
    >
      {text.split("").map((char, index) => (
        <motion.span key={index} custom={index} variants={generateLetterVariants}>
          {char}
        </motion.span>
      ))}
    </motion.p>
  );
};

const LearnHyve = () => {
  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-6">
      <AnimatedText text={text1} />
      <motion.hr 
        className="border-black w-1/2 my-4"
        initial={{ width: 0 }}
        animate={{ width: "50%" }}
        transition={{ duration: 1 }}
      />
      <AnimatedText text={text2} />
    </div>
  );
};

export default LearnHyve;
