import { motion } from "framer-motion";
import { useState, useEffect } from "react";

const TextFillAnimation = () => {
  const [showCursor, setShowCursor] = useState(true);

  useEffect(() => {
    const interval = setInterval(() => {
      setShowCursor((prev) => !prev);
    }, 500);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="flex items-center justify-center h-screen bg-black p-5">
      <div className="relative text-2xl sm:text-3xl md:text-4xl lg:text-5xl font-bold text-transparent stroke-yellow-400 text-center leading-relaxed max-w-3xl">
        {/* Outlined Text */}
        "Education is the most powerful weapon which you can use to change the world. Keep learning, keep growing!"
        
        {/* Filling Text Animation */}
        <motion.div
          className="absolute top-0 left-0 w-0 h-full text-yellow-400 overflow-hidden leading-relaxed"
          initial={{ width: 0 }}
          animate={{ width: "100%" }}
          transition={{ duration: 3, ease: "linear" }}
        >
          "Education is the most powerful weapon which you can use to change the world. Keep learning, keep growing!"
          {showCursor && <span className="inline-block w-2 h-6 bg-yellow-400 ml-1 animate-blink"></span>}
        </motion.div>
      </div>

      {/* Blinking Cursor Animation */}
      <style>
        {`
          .stroke-yellow-400 {
            -webkit-text-stroke: 1px #ffcc00;
          }
          @keyframes blink {
            50% { opacity: 0; }
          }
          .animate-blink {
            animation: blink 1s infinite;
          }
        `}
      </style>
    </div>
  );
};

export default TextFillAnimation;
