import { motion } from 'framer-motion';
import { useEffect, useState } from 'react';
import './App.css';

const TextAnimation = () => {
  const [typedText, setTypedText] = useState('');
  const fullText = "Discover a dynamic gamified learning platform that equips students with advanced tools, essential life and soft skills, a vibrant community, and hands-on experiences.";

  useEffect(() => {
    let index = 0;
    const interval = setInterval(() => {
      if (index < fullText.length) {
        setTypedText((prev) => prev + fullText[index]);
        index++;
      } else {
        clearInterval(interval);
      }
    }, 50);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="text-center p-10">
      {/* Large Text with Fill Animation */}
      <motion.h1
        className="text-5xl font-bold"
        initial={{ WebkitTextStroke: '2px black', color: 'transparent' }}
        animate={{ color: 'black' }}
        transition={{ duration: 2 }}
      >
        Are you ready to change the <span className="text-orange-500">landscape of education</span> with LearnHyve?
      </motion.h1>

      {/* Small Text with Typewriter Animation */}
      <p className="text-gray-400 text-lg mt-5">{typedText}</p>
    </div>
  );
};

export default TextAnimation;
