import { motion } from "framer-motion";
import { useState } from "react";
import { useEffect } from "react";

const TextReveal = () => {
  const [isHovered, setIsHovered] = useState(false);

  useEffect(() => {
    setTimeout(() => setIsHovered(true), 500);
  }, []);

  const headingVariants = {
    initial: { color: "rgba(0, 0, 0, 0)", textShadow: "0px 0px 10px #f97316" },
    animate: {
      color: "#000",
      textShadow: "none",
      transition: { duration: 2, ease: "easeInOut" },
    },
  };

  const paragraphVariants = {
    initial: { color: "gray" },
    animate: {
      color: "black",
      transition: { duration: 3, staggerChildren: 0.05 },
    },
  };

  return (
    <div className="flex flex-col items-start justify-center min-h-screen p-10 bg-gradient-to-br from-blue-200 to-purple-200">
      <motion.h1
        className="text-5xl font-bold"
        variants={headingVariants}
        initial="initial"
        animate={isHovered ? "animate" : "initial"}
      >
        Are you ready to change the <br />
        <span className="text-orange-500">landscape of education</span> with
        LearnHyve?
      </motion.h1>
      
      <motion.p
        className="mt-4 text-lg max-w-2xl"
        variants={paragraphVariants}
        initial="initial"
        animate={isHovered ? "animate" : "initial"}
      >
        Discover a dynamic gamified learning platform that equips students with
        advanced tools, essential life and soft skills, a vibrant community, and
        hands-on experiences. Unlock a new way of learning with LearnHyve!
      </motion.p>
    </div>
  );
};

export default TextReveal;
