import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";

const TypingEffect = () => {
  const text = "Are you ready to change the landscape of education with LearnHyve?";
  const [displayedText, setDisplayedText] = useState("");
  const [cursorVisible, setCursorVisible] = useState(true);

  useEffect(() => {
    let index = 0;

    const interval = setInterval(() => {
      if (index < text.length) {
        setDisplayedText((prev) => prev + text[index]);
        index++;
      } else {
        clearInterval(interval);
      }
    }, 50); // Typing speed (adjust if needed)

    return () => clearInterval(interval);
  }, []);

  // Cursor blinking effect
  useEffect(() => {
    const cursorInterval = setInterval(() => {
      setCursorVisible((prev) => !prev);
    }, 500);

    return () => clearInterval(cursorInterval);
  }, []);

  return (
    <div style={{ textAlign: "center", padding: "50px", fontSize: "24px", fontWeight: "bold" }}>
      <motion.span
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        {displayedText}
      </motion.span>
      {cursorVisible && <motion.span>|</motion.span>} {/* Blinking Cursor */}
    </div>
  );
};

export default TypingEffect;
