import React, { useState, useEffect, useRef } from "react";
import blackLogo from "../images/blackLogo.png";
import "./navBar.css";

const Nav = () => {
    const [searchTerm, setSearchTerm] = useState(""); // For search input
    const [isDropdownVisible, setDropdownVisible] = useState(false); // To toggle dropdown visibility
    const dropdownRef = useRef(null); // To detect clicks outside
    const searchBarRef = useRef(null);

    const skills = [
        "Communication Skills",
        "Emotional Intelligence",
        "Leadership & Teamwork",
        "Spoken English",
        "Stock Market & Investment",
        "Startup & Entrepreneurship",
        "Problem Solving & Critical Thinking",
        "Money Management",
        "Health & Nutrition",
        "Robotics & Automation",
        "AI Tools",
        "Yoga"
    ];

    // Filtered dropdown options
    const filteredOptions = skills.filter(skill =>
        skill.toLowerCase().includes(searchTerm.toLowerCase())
    );

    // Show dropdown on focus
    const handleFocus = () => {
        if (searchTerm.trim() !== "") setDropdownVisible(true);
    };

    // Hide dropdown when clicking outside
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target) && 
                searchBarRef.current && !searchBarRef.current.contains(event.target)) {
                setDropdownVisible(false);
            }
        };
        document.addEventListener("click", handleClickOutside);
        return () => document.removeEventListener("click", handleClickOutside);
    }, []);

    // Update search input when clicking an option
    const handleOptionClick = (option) => {
        setSearchTerm(option);
        setDropdownVisible(false);
    };

    // Handle Search Button Click
    const handleSearch = () => {
        if (searchTerm.trim()) {
            const formattedSearch = searchTerm.toLowerCase().trim().replace(/\s+/g, '-');
            window.location.href = `school.html#${formattedSearch}`;
        } else {
            alert("Please enter a skill name.");
        }
    };

    return (
        <div className="nav">
            <div className="nav-container">
                <a href="#" className="blackLogo">
                    <img src={blackLogo} alt="LearnHyve" />
                </a>

                {/* Start Searching for Skills */}
                <div className="s-course">
                    <div className="search-container" ref={dropdownRef}>
                        <div className="search-bar-container">
                            <input
                                type="text"
                                id="search-bar"
                                ref={searchBarRef}
                                autoComplete="off"
                                placeholder="Search for skills..."
                                value={searchTerm}
                                onChange={(e) => setSearchTerm(e.target.value)}
                                onFocus={handleFocus}
                            />
                            <button id="search-button" onClick={handleSearch}>Search</button>
                        </div>

                        {/* Dropdown Options (Scrollable) */}
                        {isDropdownVisible && filteredOptions.length > 0 && (
                            <ul id="dropdown-options" className="dropdown-options">
                                {filteredOptions.map((skill, index) => (
                                    <li key={index} onClick={() => handleOptionClick(skill)}>
                                        {skill}
                                    </li>
                                ))}
                            </ul>
                        )}
                    </div>
                </div>
                {/* End of Searching for Skills */}

                <div className="nav-links">
                    <a href="#">Home</a>
                    <a href="#">Features</a>
                    <a href="#">Workshops</a>
                    <a href="#">Blogs</a>
                    <a href="#">About</a>
                    <button className="contact-btn">Contact Us</button>
                </div>
                <div className="menu-toggle">â˜°</div>
            </div>
        </div>
    );
};

export default Nav;
